mapper.select:

WHERE GROUP_CODE = '1000'
  AND CODE = #{code}
  AND ROWNUM = 1


WHERE PARENT_CODE = #{parentCode}
AND CODE = #{code}
AND ROWNUM = 1



WHERE PARENT_CODE = #{parentCode}


ORDER BY GROUP_CODE,
TO_NUMBER(DISPLAY)



    WHERE PARENT_CODE = #{parentCode}

ORDER BY GROUP_CODE,
TO_NUMBER(DISPLAY)
