mapper.select:


WHERE delete_id IS NULL

    AND name = #{name}


    AND type = #{type}

mapper.update:

UPDATE brewduckdb.FERMENTABLE


        korean_name = #{koreanName},


        version = #{version}


        type = #{type}


        amount = #{amount}


        yield = #{yield}


        color = #{color}


        add_after_boil = #{addAfterBoil}


        orgin = #{orgin}


        supplier = #{supplier}


        notes = #{notes}


        coarse_fine_diff = #{coarseFineDiff}


        moisture = #{moisture}


        diastatic_power = #{diastaticPower}


        protein = #{protein}


        max_in_batch = #{maxInBatch}


        recommend_mash = #{recommendMash}


        ibu_gal_per_lb = #{ibuGalPerLb}


        update_id = #{updateId},


        update_date = now()


WHERE name = #{name}


UPDATE brewduckdb.FERMENTABLE
   SET delete_id = #{deleteId}
      ,delete_date = now()
 WHERE name = #{name}
